print(os.path.dirname(os.getcwd())+"\data\picture")
print(os.path.defpath)

def increase_counter():
    def increase():         #定义一个还有自然数算法的生成器,企图使用next来完成不断调用的递增
        n = 0
        while True:
            n = n+1
            yield n

    it = increase()        #一定要将生成器转给一个(生成器)对象,才可以完成,笔者第一次做,这里一直出问题,
                                  #一直没解开,看到别人做的才更改完成
    def counter():        #再定义一内函数
        return next(it)  #调用生成器的值,每次调用均自增（
                         #注意：it不要加()括号调用会出错的
    return counter()
c = increase_counter()
